package com.jiangsilu.leaf.config;import com.jiangsilu.leaf.SnowflakeIDGenImpl;import com.jiangsilu.leaf.SnowflakeZookeeperHolder;import com.jiangsilu.leaf.propertis.LeafProperties;import org.springframework.beans.factory.annotation.Value;import org.springframework.boot.autoconfigure.condition.ConditionalOnMissingBean;import org.springframework.boot.context.properties.EnableConfigurationProperties;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import javax.annotation.Resource;/** * @author: sixUncle * @Version 1.0 **/@Configuration@EnableConfigurationProperties(LeafProperties.class)public class LeafConfig {    @Resource    private LeafProperties leafProperties;    @Value("${spring.application.name}")    private String appName;    @Bean    @ConditionalOnMissingBean({SnowflakeZookeeperHolder.class})    public SnowflakeZookeeperHolder snowflakeZookeeperHolder() {        return new SnowflakeZookeeperHolder(leafProperties, appName);    }    @Bean    @ConditionalOnMissingBean({SnowflakeIDGenImpl.class})    public SnowflakeIDGenImpl snowflakeIDGen(SnowflakeZookeeperHolder snowflakeZookeeperHolder) throws Exception {        return new SnowflakeIDGenImpl(snowflakeZookeeperHolder);    }}