package com.jiangsilu.leaf.exception;/** * 接口异常 * * @author: sixUncle * @Version 1.0 **/public class LeafException extends RuntimeException {    private String message;    private Integer code;    public LeafException(Integer code, String message) {        super(message);        this.code = code;        this.message = message;    }    @Override    public String getMessage() {        return message;    }    public Integer getCode() {        return code;    }}